data Attack = Attack { atType :: AttackType, 
                       atDamageType :: DamageType, 
                       atDice :: D.Dice }

data Monster = Monster { moName :: B.ByteString, 
                         moSymbol :: Char, 
                         moBaseLevel :: Int, 
                         moSpeed :: Int, 
                         moAC :: Int, 
                         moMR :: Int, 
                         moAlign :: Int, 
                         moGeneratesInGehennom :: Bool, 
                         moLeavesCorpse :: Bool, 
