                         moWeight :: Int, 
                         moNutrition :: Int, 
                         moSize :: MonsterSize, 
                         moResistances :: [Resistance], 
                         moConferred :: [Resistance], 
                         moFlags :: [MonsterFlag], 
                         moColor :: Color }

